pub struct Solution {}

use crate::AoCSolution;

impl AoCSolution for Solution {
    fn day(&self) -> i8 { 1 }

    fn part1(&self) -> String {
        INPUT
            .chars()
            .map(|c| match c {
                '(' => 1,
                ')' => -1,
                _ => 0,
            })
            .sum::<i32>().to_string()
    }

    fn part2(&self) -> String {
        let mut floor = 0;
        for (i, c) in INPUT.chars().enumerate() {
            floor += match c {
                '(' => 1,
                ')' => -1,
                _ => 0,
            };
            if floor < 0 {
                return (i + 1).to_string();
            }
        }
        
        String::from("-1")
    }
}
